import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { drawCard, placeCard, startGame } from '../services/api';
import Card from './Card';
import Timeline from './Timeline';

const Game = () => {
    const { gameId } = useParams();
    const [game, setGame] = useState(null);
    const [currentCard, setCurrentCard] = useState(null);
    const [cards, setCards] = useState([]);

    useEffect(() => {
        // Fetch game details and set the game state here
    }, [gameId]);

    const handleDrawCard = async () => {
        try {
            const token = localStorage.getItem('token');
            const player = { id: 'PLAYER_ID_FROM_CONTEXT' }; // replace with actual player id
            const response = await drawCard(gameId, player);
            setCurrentCard(response.data);
        } catch (error) {
            console.error(error);
        }
    };

    const handlePlaceCard = async (card, position) => {
        try {
            const request = {
                player: { id: 'PLAYER_ID_FROM_CONTEXT' }, // replace with actual player id
                card,
                position
            };
            const response = await placeCard(gameId, request);
            setCards(response.data.timeline); // Update timeline with response data
            setCurrentCard(null); // Clear the current card after placing
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Game ID: {gameId}</h2>
            {currentCard && <Card card={currentCard} />}
            <button onClick={handleDrawCard}>Draw Card</button>
            <Timeline cards={cards} onDrop={handlePlaceCard} />
        </div>
    );
};

export default Game;
